import React, { useEffect, useState } from 'react';

function Results(props){
  const [searchValue, setSearch] = useState('');
  const [selectedBook, setSelection] = useState('');

  useEffect(()=> {
    console.log('read effect!');
    console.log(props);
    console.log('props results!');
    console.log(props.results);
    // localVar = props.results.items;
  });

  const handleClick = event => {
    console.log('book has been clicked');
    console.log(event);
    console.log(event.target.id);
    props.displayBookPage(event.target.id);
    // props.displayBookPage({isbn})
  }

  if (!props.results){
    return (<div></div>);
  }

  return(

    <div>
    <ul id='resultSpace'>
      {props.results.map(book => (
        <li
          id={book.id}
          class='bookResult'
          onClick={handleClick}
        >
          <img
          class='bookThumb'
          height='125'
          src={book.imageLinks ? book.imageLinks.thumbnail : 'null'}
          />
          <div class='resultInfo'>
            {book.title}
            <br/>
            by {book.authors}
          </div>
        </li>
      ))}
    </ul>
      <p>{searchValue}</p>
    </div>

  )
}

export default Results;
