import React, { useEffect, useState } from 'react';
import Rating from '@material-ui/lab/Rating';
import Link from 'react-router-dom';

const BookRating = () => {
  const [book, setBook] = useState();
  const [rating, setRating] = useState();

  const handleClick = (starRating) => {
    // e.preventDefault();
    console.log('this should only happen on the change!');
    console.log(starRating);
    setRating(starRating);

    console.log(`The rating ${rating} was clicked`);
    if (book && rating){
      // ratingServices.addRatingToDb(book, rating);
    }
  };

  useEffect(() => {
    // TODO For now we're setting a default rating of 4 when the component mounts, but this function will check for the user's rating of a specific volume.
    setRating(4);
  }, []);

  return(
    <Rating
      name="hover-feedback"
      defaultValue={rating}
      precision={0.5}
      onChange={(event, newValue) => {
        handleClick(newValue);
      }}
    />
  )
};

export default BookRating;
