  const [book, setBook] = useState();
  const [rating, setRating] = useState();

  const handleClick = (e) => {
    e.preventDefault();

    console.log(`The rating ${rating} was clicked`);
    if (book && rating){
      ratingServices.addRatingToDb(book, rating);
    }
  };

  return(
    <Rating
      name="hover-feedback"
      value="3"
      onClick={ ({target}) => setRating(target.value)}
    />
  )
};
