import './App.css';
import React, { useState } from 'react';

import SearchBar from './components/SearchBar';
import Results from './components/Results';



function App(props) {

  const [searchValue, setSearch] = useState('search for something');
  const [results, updateResults] = useState([{title: 'ivanhoe', authors: 'Ballzach', imageLinks: ''}]);

  const searchForBook = searchValue => {
    setSearch(searchValue);
    console.log(searchValue);
    console.log('bubblin up!');
    let searchString = 'https://www.googleapis.com/books/v1/volumes?q='
    + searchValue.searchValue + '&key=' +
    process.env.REACT_APP_GOOGLE_BOOKS_KEY;

    console.log('cliyuck! ' + searchString);
    fetch(searchString)
      .then(res => res.json())
        .then(
          (result) =>
          {
            // reduce our search results to the volume info
            let resultItems = result.items.map(a => a.volumeInfo);
            console.log(resultItems);
            updateResults(resultItems);
            console.log(result);
            // props.updateResult({searchResult: result});
            // console.log('{props.searchResult}');
            // showResults(props.searchResult);
          }
        )

  }

  return (
    <div className='App'>
      <header className='App-header'>
      <p>BookBug</p>
      <SearchBar searchForBook={searchForBook}/>
      <></>
      </header>
      <div className='body'>
        <Results results={results}/>
      </div>
    </div>
  );
}

export default App;
