import React, {useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import fire from '../fire.js'

function Login(props){
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [confirmPassword, setConfirmPassword] = useState();
  const [newAccount, setAccountStatus] = useState(false);

  const openRegistration = () => {
    setAccountStatus(true);
  }

  const closeRegistration = () => {
    setAccountStatus(false);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newAccount)
    {
      fire.auth.createUserAndWithEmailAndPassword(email, password).then(cred => {
        console.log(cred);
      });
    } else {
      console.log(`submitted email: ${email} password: ${password}`);
      fire.auth().signInWithEmailAndPassword(email, password)
      .catch((error) => {
        console.error('Incorrect username or password');
      });
    }
  }


return(
    <div>
      <h1>Please log in</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Email:</p>
          <input
          type='text'
          onChange={({ target }) =>
          setEmail(target.value)}
          placeholder='Email'
          />
        </label>
        <label>
          <p>Password:</p>
          <input
            type='password'
            onChange={({ target }) =>
            setPassword(target.value)}
            placeholder='Password'
          />
        </label>
        {newAccount &&
          <label>
            <p>Confirm Password:</p>
            <input
              type='password'
              onChange={({ target }) =>
              setConfirmPassword(target.value)}
              placeholder='Confirm Password'
            />
          </label>
        }
        <div>
            <button type='submit'>Submit</button>
        {!newAccount &&
          <div>
            No account? <Link onClick={openRegistration}>Sign up here</Link>.
          </div>
        }
        {newAccount &&
          <div>
            Existing member? <Link onClick ={closeRegistration}>Log in here</Link>.
          </div>
        }
        </div>
      </form>
    </div>
  )
}

export default Login;
