import React from 'react';
// import classnames from 'classnames';


class SearchBar extends React.Component {
  constructor(props){
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  searchForBook = () => {
    console.log('cliyuck! ' + this.state.value);
  }

  render(){
    return(
      <div>
        <h1>Hey it worked!</h1>
        <input id="bookSearch" value={this.state.value} onChange={this.handleChange} />
        <button
          onClick={this.searchForBook}
        >Search</button>
      </div>);
  }
};

export default SearchBar;
