import React, { useEffect, useState } from 'react';
// import classnames from 'classnames';


function SearchBar(props){
  const [searchValue, setSearch] = useState(props.searchValue);

  const handleChange = event => {
    props.setSearch(event.target.value);
  };

  const searchForBook = () => {
    let searchString = 'https://www.googleapis.com/books/v1/volumes?q='
    + '{props.searchValue}' + '&key=' +
    process.env.REACT_APP_GOOGLE_BOOKS_KEY;

    console.log('cliyuck! ' + searchString);
    fetch(searchString)
      .then(res => res.json())
        .then(
          (result) =>
          {
            props.updateResult({searchResult: result});
            console.log('{props.searchResult}');
            showResults(props.searchResult);
          }
        )
  }

  const showResults = (result) => {
    console.log('wow');
  }

  return(
    <div>
      <h1>Hey it worked!</h1>
      <input
        id="bookSearch"
        placeholder="find a book"
        value={props.searchValue}
        onChange={props.setSearch}
      />
      <button
        class="search-button"
        onClick={props.searchForBook}
      >Search</button>
    </div>);
};

export default SearchBar;
