// Search for a specific volume by id and return its volume info
export const fetchVolumeInfo = async (id, setActiveBook) => {

  const searchString = 'https://www.googleapis.com/books/v1/volumes/' + id + '?key=' +
  process.env.REACT_APP_GOOGLE_BOOKS_KEY;

  const result = await
  fetch(searchString)
    .then(res => res.json())
      .then(
        (result) =>
        {
          console.log(result.volumeInfo);
          return result.volumeInfo;
          setActiveBook(id);
        }
      )
}
