import React, { useEffect, useState } from 'react';
// import classnames from 'classnames';


function SearchBar({passin}){
  const [searchValue, setSearch] = useState('');
  const [searchResult, updateResult] = useState('');

  const handleChange = event => {
    setSearch(event.target.value);
  };

  const searchForBook = () => {
    let searchString = 'https://www.googleapis.com/books/v1/volumes?q='
    + {searchValue} + '&key=' +
    process.env.REACT_APP_GOOGLE_BOOKS_KEY;

    console.log('cliyuck! ' + searchString);
    fetch(searchString)
      .then(res => res.json())
        .then(
          (result) =>
          {
            updateResult({searchResult: result});
            console.log({searchResult});
            showResults(searchResult);
          }
        )
  }

  const showResults = (result) => {
    console.log('wow');
  }

  return(
    <div>
      <h1>Hey it worked!</h1>
      <input
        id="bookSearch"
        placeholder="find a book"
        value={searchValue}
        onChange={handleChange}
      />
      <button
        class="search-button"
        onClick={searchForBook}
      >Search</button>
    </div>);
};

export default SearchBar;
