import React from 'react';
// import classnames from 'classnames';


class SearchBar extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: ''
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({searchValue: event.target.value});
    console.log(event.target.value);
  }

  searchForBook = () => {
    let searchString = 'https://www.googleapis.com/books/v1/volumes?q='
    + this.state.searchValue + '&key=' +
    process.env.REACT_APP_GOOGLE_BOOKS_KEY

    console.log('cliyuck! ' + searchString);
    fetch(searchString)
      // .then(res => res.json())
        .then(
          (result) =>
          {
            console.log('result!');
            console.log(result);
          }
        )
  }

  render(){
    return(
      <div>
        <h1>Hey it worked!</h1>
        <input id="bookSearch" value={this.state.value} onChange={this.handleChange} />
        <button
          onClick={this.searchForBook}
        >Search</button>
      </div>);
  }
};

export default SearchBar;
