import React, { useEffect, useState } from 'react';
// import classnames from 'classnames';


function SearchBar(props){
  const [searchValue, setSearch] = useState(null);

  const handleChange = event => {
    setSearch({searchValue: event.target.value});
  };

  const searchForBook = () => {
    let searchString = 'https://www.googleapis.com/books/v1/volumes?q='
    + {searchValue} + '&key=' +
    process.env.REACT_APP_GOOGLE_BOOKS_KEY;

    console.log('cliyuck! ' + searchString);
    fetch(searchString)
      .then(res => res.json())
        .then(
          (result) =>
          {
            this.setState({results: result});
            console.log(this.state);
            this.showResults(result);
          }
        )
  }

  const showResults = (result) => {
    console.log('wow');
  }

  return(
    <div>
      <h1>Hey it worked!</h1>
      <input
        id="bookSearch"
        placeholder="find a book"
        value={searchValue}
        onChange={this.handleChange}
      />
      <button
        class="search-button"
        onClick={this.searchForBook}
      >Search</button>
    </div>);
};

export default SearchBar;
